import React, { useState } from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = (props) => {
    const [filteredYear, setFilteredYear] = useState();

    const filteredYearHandler = (event) => {
        setFilteredYear(event.target.value)
    }

    const filterHandler = (event) => {
        event.preventDefault();
        const filtered = {
            year: filteredYear
        };
        props.onFilteredYear(filtered)
    }

    return (
        <div className='expenses-filter'>
            <div className='expenses-filter__control'>
                <label>Filter by year</label>
                <select onSubmit={filterHandler}>
                    <option value='2025' onChange={filteredYearHandler}>2025</option>
                    <option value='2024' onChange={filteredYearHandler}>2024</option>
                    <option value='2023' onChange={filteredYearHandler}>2023</option>
                    <option value='2022' onChange={filteredYearHandler}>2022</option>
                </select>
            </div>
        </div >
    );
};

export default ExpensesFilter;
